import React, {Component} from "react";
import Axios from 'axios';
import './App.css';

class _Content_page extends Component{
    constructor(props) {
        super(props);
        this.state = { 
            posts: null,
            usernames: null,
            displayedPosts: null
        }
    }

    componentDidMount() {
        const port = "http://localhost:3001"

        //fetch all posts here
        Axios.get(`${port}/api/getallposts`).then((res) => {
            this.setState({ posts: res.data})
            // console.log(this.state.posts)
        }).then(
            Axios.get(`${port}/api/getallusers`).then((res) => {          
                this.setState({ usernames: res.data })
                this.updateDisplayedPosts();
            })
        )
    }

    updateDisplayedPosts = () => {
        let displayedPost = {
            id: "",
            user: "",
            title: "",
            content: "",
            creationDate: ""                
        }
        let displayedPosts = [];

        this.state.posts.forEach(post => {
            let username = this.state.usernames.find((user) => {
                return (user.id === post.userId)
            }).username
            displayedPost.id = post.id;
            displayedPost.user = username;
            displayedPost.title = post.title;
            displayedPost.content = post.content;
            displayedPost.creationDate = post.creationDate;

            displayedPosts.push(displayedPost);
        });
        this.setState({displayedPosts: displayedPosts})
        console.log(this.state.displayedPosts)
    }

    renderPosts = () => {
        // console.log(this.state.posts)
        let output = this.state.posts.map(post => {
            return(
                <div key={`${post.id}_container`}>
                    <div className="container" key={post.id}>
                        <div className='row'>
                            <label className="col-md" style={{textAlign: "left", fontSize: "24px"}}>Title: {post.title}</label>
                            <div className="col-md-6"></div>
                            <label className="col-md pt-2" style={{textAlign: "right"}}>Date: {post.creationDate}</label>
                        </div>
                        <div className='row pb-4'>
                            <textarea readOnly={true} defaultValue={post.content}></textarea>
                        </div>
                    </div>
                    <div className="row mb-3" key={`${post.id}_space`}></div>
                </div>

            )
        });
        return(
            <div>
                {output}
            </div>
        );
    }   


    render() {

        return(
            <div className="content_page>">
                <h2>Content for Consumption</h2>

                {(() => {
                    switch (this.state.posts) {
                        case null:
                            return(
                                <div>
                                </div>
                            )
                        default:
                            return (
                                <div>
                                    <this.renderPosts />
                                </div>
                            )
                    }
                })()}
            </div>
        )
    }
}

export default _Content_page;




//USER STORIES
